package huggingface

import (
	"fmt"
	"testing"

	"start-feishubot/initialization"
)

const (
	blockedImage = ""
)

func TestGenerateOneImage(t *testing.T) {
	config := initialization.LoadConfig("../../config.yaml")
	if len(config.HuggingFaceApiKeys) == 0 {
		t.Errorf("Test HuggingFace GenerateImage failed with error: no api keys available")
	}
	fmt.Println(config.HuggingFaceApiKeys)
	hf := NewHuggingFace(*config)
	input := "Astronaut riding a horse"
	imgBase64, err := hf.GenerateImage(input)

	if err != nil {
		t.Errorf("Test HuggingFace GenerateImage failed with error: %v", err)
	}

	if len(imgBase64) == 0 {
		t.Errorf("Test HuggingFace GenerateImage failed with empty error")
	}
	fmt.Printf("img base64, %s", imgBase64)
	if imgBase64 == blockedImage {
		t.Errorf("Test HuggingFace GenerateImage failed with blocked img error")
	}
}
